mapper.select:

select count(id)
from member
where id=#{memberId}


select count(*)
from product
where id=#{productId}

mapper.update:

    		UPDATE PERSONAL_CART
			SET PRODUCT_CNT = #{productCnt}
			WHERE member_id= #{memberId} and product_id=#{productId}


    		UPDATE GROUP_MEMBER_CART
			SET PRODUCT_CNT = #{productCnt}
			WHERE member_id= #{memberId} and product_id=#{productId} and group_id=#{groupId}
